import React, { useState, useEffect } from "react";
import Question from "./Question";
import Results from "./Results";
import questionsData from "../questions.txt";

const Quiz = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [timeLeft, setTimeLeft] = useState(30); // Час на відповідь в секундах
  const [timerRunning, setTimerRunning] = useState(false);

  // Завантаження питань з текстового файлу
  useEffect(() => {
    fetch(questionsData)
      .then((response) => response.text())
      .then((text) => {
        const lines = text.trim().split("\n");
        const parsedQuestions = lines.map((line) => {
          const [question, ...rest] = line.split("|");
          const correctAnswer = rest.pop();
          const options = rest;
          return {
            question,
            options,
            correctAnswer,
          };
        });
        setQuestions(parsedQuestions);
      });
  }, []);

  // Таймер для кожного питання
  useEffect(() => {
    if (timerRunning && timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else if (timeLeft === 0) {
      handleAnswerClick(null); // Автоматично переходимо до наступного питання, якщо час закінчився
    }
  }, [timeLeft, timerRunning]);

  // Запуск таймера при переході до нового питання
  useEffect(() => {
    if (questions.length > 0 && currentQuestionIndex < questions.length) {
      setTimeLeft(30); // Скидаємо таймер для нового питання
      setTimerRunning(true);
    }
  }, [currentQuestionIndex, questions]);

  const handleAnswerClick = (selectedOption) => {
    setTimerRunning(false);
    const currentQuestion = questions[currentQuestionIndex];
    if (selectedOption === currentQuestion.correctAnswer) {
      setScore(score + 1);
    }

    const nextQuestionIndex = currentQuestionIndex + 1;
    if (nextQuestionIndex < questions.length) {
      setCurrentQuestionIndex(nextQuestionIndex);
    } else {
      setShowResults(true);
    }
  };

  if (questions.length === 0) {
    return <div>Завантаження...</div>;
  }

  if (showResults) {
    return <Results score={score} totalQuestions={questions.length} />;
  }

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="quiz-container">
      <div className="timer">
        Час: <span className={timeLeft <= 5 ? "red-text" : ""}>{timeLeft}</span>
      </div>
      <Question
        question={currentQuestion.question}
        options={currentQuestion.options}
        onAnswerClick={handleAnswerClick}
      />
    </div>
  );
};

export default Quiz;
