import React, { useState, useEffect } from "react";
import "./App.css";
import Quiz from "./components/Quiz";

function App() {
  const [backgroundImage, setBackgroundImage] = useState("");
  const [currentTime, setCurrentTime] = useState(new Date());
  const [isQuizStarted, setIsQuizStarted] = useState(false); // Новий стан для відстеження початку квізу

  // Масив з назвами файлів фонових зображень
  const backgrounds = [
    "background_1.jpg",
    "background_2.jpg",
    "background_3.jpg",
    "background_4.jpg",
    "background_5.jpg",
    "background_6.jpg",
    "background_7.jpg",
    "background_8.jpg",
    "background_9.jpg",
    "background_10.jpg",
    "background_11.jpg",
  ];

  useEffect(() => {
    const randomIndex = Math.floor(Math.random() * backgrounds.length);
    const selectedBackground = backgrounds[randomIndex];
    setBackgroundImage(selectedBackground);
  }, []);

  useEffect(() => {
    if (backgroundImage) {
      document.body.style.backgroundImage = `url(${process.env.PUBLIC_URL}/images/${backgroundImage})`;
    }
  }, [backgroundImage]);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const formatDate = (date) => {
    const options = {
      year: "numeric",
      month: "long",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit",
    };
    return date.toLocaleDateString("uk-UA", options);
  };

  return (
    <div className="App">
      <div className="date-time-display">{formatDate(currentTime)}</div>
      <header className="App-header">
        <h1>{isQuizStarted ? "Перевірте свої знання!" : "Яскравий Квіз"}</h1>
      </header>
      <main>
        <Quiz onQuizStart={() => setIsQuizStarted(true)} />
      </main>
    </div>
  );
}

export default App;
