import React, { useState } from "react";

const Question = ({
  question,
  options,
  onAnswerClick,
  isMuted,
  answerFeedback,
}) => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleOptionClick = (option) => {
    if (!answerFeedback.answered) {
      setSelectedOption(option);
      onAnswerClick(option);

      if (!isMuted) {
        // Використовуємо правильний шлях
        const audio = new Audio(
          `/sounds/${answerFeedback.isCorrect ? "correct" : "incorrect"}.mp3`
        );
        audio.play();
      }
    }
  };

  return (
    <div className="question-container">
      <h2>{question}</h2>
      <div className="options-container">
        {options.map((option, index) => {
          let buttonClass = "option-button";

          if (answerFeedback.answered) {
            if (option === answerFeedback.correctAnswer) {
              buttonClass += " correct-answer";
            } else if (option === selectedOption) {
              buttonClass += " incorrect-answer";
            } else {
              buttonClass += " disabled-option";
            }
          }

          return (
            <button
              key={index}
              className={buttonClass}
              onClick={() => handleOptionClick(option)}
              disabled={answerFeedback.answered}
            >
              {option}
            </button>
          );
        })}
      </div>
    </div>
  );
};

export default Question;
