import React, { useState, useEffect, useCallback } from "react";
import Question from "./Question";
import Results from "./Results";

const Quiz = ({
  onQuizStart,
  onQuizEnd,
  onQuizLoad,
  onAnswerFeedback,
  isMuted,
  onHeaderChange,
}) => {
  const [allQuestions, setAllQuestions] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [timeLeft, setTimeLeft] = useState(30);
  const [timerRunning, setTimerRunning] = useState(false);
  const [isStarted, setIsStarted] = useState(false);
  const [userName, setUserName] = useState("");
  const [error, setError] = useState("");
  const [animationClass, setAnimationClass] = useState("");
  const [startTime, setStartTime] = useState(null);
  const [totalTime, setTotalTime] = useState(null);

  const [selectedTopic, setSelectedTopic] = useState(null);
  const [quizSize, setQuizSize] = useState(null);

  const [loadingError, setLoadingError] = useState(null);

  const swearWords = ["матюк", "лайка", "слово", "ненорматив", "fuck", "shit"];
  const topics = [
    "Загальні питання",
    "Географія",
    "Історія",
    "Біологія",
    "Фізика",
    "Хімія",
    "Література",
    "Мова",
    "Інформатика",
    "Математика",
  ];

  const topicFileMap = {
    "Загальні питання": "general.js",
    Географія: "geography.js",
    Історія: "history.js",
    Біологія: "biology.js",
    Фізика: "physics.js",
    Хімія: "chemistry.js",
    Література: "literature.js",
    Мова: "ukrainian_language.js",
    Інформатика: "computer_science.js",
    Математика: "mathematics.js",
  };

  const handleAnswerClick = useCallback(
    (selectedOption) => {
      setTimerRunning(false);
      const currentQuestion = questions[currentQuestionIndex];

      const isCorrect = selectedOption === currentQuestion.correctAnswer;
      onAnswerFeedback(isCorrect);

      if (isCorrect) {
        setScore((prevScore) => prevScore + 1);
      }

      setAnimationClass("slide-out");

      setTimeout(() => {
        const nextQuestionIndex = currentQuestionIndex + 1;
        if (nextQuestionIndex < questions.length) {
          setCurrentQuestionIndex(nextQuestionIndex);
        } else {
          const endTime = new Date();
          const elapsedTimeInSeconds = Math.floor((endTime - startTime) / 1000);
          setTotalTime(elapsedTimeInSeconds);
          setShowResults(true);
          onQuizEnd();
        }
      }, 700);
    },
    [questions, currentQuestionIndex, onAnswerFeedback, startTime, onQuizEnd]
  );

  useEffect(() => {
    if (selectedTopic) {
      setLoadingError(null);

      const fileName = topicFileMap[selectedTopic];

      if (!fileName) {
        console.error("Помилка: Немає відповідного файлу для обраної теми.");
        setLoadingError(
          "Не вдалося завантажити питання: назва файлу некоректна."
        );
        return;
      }

      import(`../data/${fileName}`)
        .then((module) => {
          setAllQuestions(module.questions);
          onQuizLoad?.(selectedTopic);
        })
        .catch((error) => {
          console.error("Помилка завантаження питань:", error);
          setLoadingError(
            "Не вдалося завантажити питання. Перевірте, чи існують файли."
          );
        });
    }
  }, [selectedTopic, onQuizLoad]);

  // Змінюємо заголовок на "Оберіть кількість питань" після вибору теми
  useEffect(() => {
    if (selectedTopic && allQuestions.length > 0) {
      onHeaderChange("Оберіть кількість питань");
    }
  }, [selectedTopic, allQuestions, onHeaderChange]);

  // Змінюємо заголовок на "Як тебе звати?" після вибору кількості питань
  useEffect(() => {
    if (quizSize) {
      onHeaderChange("Як тебе звати?");
    }
  }, [quizSize, onHeaderChange]);

  useEffect(() => {
    if (timerRunning && timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else if (timeLeft === 0) {
      handleAnswerClick(null);
    }
  }, [timeLeft, timerRunning, handleAnswerClick]);

  useEffect(() => {
    if (
      isStarted &&
      questions.length > 0 &&
      currentQuestionIndex < questions.length
    ) {
      setTimeLeft(30);
      setTimerRunning(true);
      setAnimationClass("slide-in");
    }
  }, [currentQuestionIndex, questions, isStarted]);

  useEffect(() => {
    if (showResults && !isMuted) {
      const audio = new Audio(process.env.PUBLIC_URL + "/sounds/finish.mp3");
      audio.play().catch((e) => console.error("Could not play sound:", e));
    }
  }, [showResults, isMuted]);

  const getRandomQuestions = (count) => {
    const shuffled = [...allQuestions].sort(() => 0.5 - Math.random());
    return shuffled.slice(0, count);
  };

  const validateName = (name) => {
    const trimmedName = name.trim();
    const ukrainianRegex = /^[А-ЯІЇЄҐа-яіїєґ\s]+$/;
    const words = trimmedName.split(/\s+/);

    if (trimmedName === "") {
      return "Будь ласка, введіть своє ім'я.";
    }
    if (!ukrainianRegex.test(trimmedName)) {
      return "Ім'я має містити лише українські літери.";
    }
    if (words.length > 2) {
      return "Ім'я може складатися з одного або двох слів.";
    }
    if (swearWords.some((word) => trimmedName.toLowerCase().includes(word))) {
      return "Будь ласка, оберіть інше ім'я.";
    }
    if (
      trimmedName.length > 2 &&
      !/[аеиіоуяєюїі]/.test(trimmedName.toLowerCase())
    ) {
      return "Ім'я не може бути випадковим набором літер.";
    }
    return "";
  };

  const handleNameChange = (e) => {
    let name = e.target.value;
    if (name.length > 0) {
      name = name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
    }
    setUserName(name);
  };

  const handleStartQuiz = (e) => {
    e.preventDefault();
    const validationError = validateName(userName);

    if (validationError) {
      setError(validationError);
    } else {
      setError("");
      setQuestions(getRandomQuestions(quizSize));
      setIsStarted(true);
      setStartTime(new Date());
      onQuizStart();
      onHeaderChange(selectedTopic); // Змінюємо заголовок на назву теми під час початку квізу
    }
  };

  if (!selectedTopic) {
    onHeaderChange("Оберіть тему");
    return (
      <div className="start-container">
        <h2>Оберіть тему</h2>
        <div className="options-container">
          {topics.map((topic, index) => (
            <button
              key={index}
              onClick={() => setSelectedTopic(topic)}
              className="option-button"
            >
              {topic}
            </button>
          ))}
        </div>
      </div>
    );
  }

  if (loadingError) {
    return (
      <div className="start-container">
        <p className="error-message">{loadingError}</p>
        <button onClick={() => setSelectedTopic(null)} className="start-button">
          Повернутися до вибору теми
        </button>
      </div>
    );
  }

  if (allQuestions.length === 0) {
    onHeaderChange("Завантаження питань...");
    return <div>Завантаження питань...</div>;
  }

  if (!quizSize) {
    return (
      <div className="start-container">
        <h2>{selectedTopic}</h2>
        <p>Оберіть кількість питань</p>
        <div className="options-container">
          <button onClick={() => setQuizSize(5)} className="option-button">
            5 питань
          </button>
          <button onClick={() => setQuizSize(10)} className="option-button">
            10 питань
          </button>
          <button onClick={() => setQuizSize(15)} className="option-button">
            15 питань
          </button>
          <button onClick={() => setQuizSize(20)} className="option-button">
            20 питань
          </button>
        </div>
      </div>
    );
  }

  if (!isStarted) {
    return (
      <div className="start-container">
        <h2>{selectedTopic}</h2>
        <p>Перевірте свої знання!</p>
        <form onSubmit={handleStartQuiz}>
          <input
            type="text"
            placeholder="Введіть своє ім'я"
            value={userName}
            onChange={handleNameChange}
            className={`name-input ${error ? "input-error" : ""}`}
          />
          {error && <p className="error-message">{error}</p>}
          <button type="submit" className="start-button">
            Почати Квіз
          </button>
        </form>
      </div>
    );
  }

  if (showResults) {
    onHeaderChange("Результати");
    return (
      <Results
        score={score}
        totalQuestions={questions.length}
        userName={userName}
        totalTime={totalTime}
      />
    );
  }

  if (questions.length === 0) {
    return <div>Завантаження питань...</div>;
  }

  const currentQuestion = questions[currentQuestionIndex];
  return (
    <div className={`quiz-container ${animationClass}`}>
      <div className="timer">
        Час: <span className={timeLeft <= 5 ? "red-text" : ""}>{timeLeft}</span>
      </div>
      <Question
        question={currentQuestion.question}
        options={currentQuestion.options}
        onAnswerClick={handleAnswerClick}
        isMuted={isMuted}
      />
      <div className="question-counter">
        Питання {currentQuestionIndex + 1} з {questions.length}
      </div>
    </div>
  );
};

export default Quiz;
