import React from "react";

const Question = ({ question, options, onAnswerClick, isMuted }) => {
  // Приймаємо isMuted
  const playSound = () => {
    if (!isMuted) {
      // Відтворюємо звук, тільки якщо не вимкнено
      const audio = new Audio(process.env.PUBLIC_URL + "/sounds/click.mp3");
      audio.play().catch((e) => console.error("Could not play sound:", e));
    }
  };

  const handleClick = (option) => {
    playSound();
    onAnswerClick(option);
  };

  return (
    <div className="question-container">
      <h2>{question}</h2>
      <div className="options-container">
        {options.map((option, index) => (
          <button
            key={index}
            className="option-button"
            onClick={() => handleClick(option)}
          >
            {option}
          </button>
        ))}
      </div>
    </div>
  );
};

export default Question;
