import React, { useState, useEffect } from "react";
import Question from "./Question";
import Results from "./Results";
import questionsData from "../questions.txt";

const Quiz = ({ onQuizStart, onQuizEnd, onQuizLoad, isMuted }) => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [timeLeft, setTimeLeft] = useState(30);
  const [timerRunning, setTimerRunning] = useState(false);
  const [isStarted, setIsStarted] = useState(false);
  const [userName, setUserName] = useState("");
  const [error, setError] = useState("");
  const [feedbackClass, setFeedbackClass] = useState("");
  const [startTime, setStartTime] = useState(null);
  const [totalTime, setTotalTime] = useState(null);
  const [animationClass, setAnimationClass] = useState(""); // Новий стан для анімації

  useEffect(() => {
    fetch(questionsData)
      .then((response) => response.text())
      .then((text) => {
        const lines = text.trim().split("\n");
        const quizTitle = lines[0];
        const questionLines = lines.slice(1);

        const parsedQuestions = questionLines.map((line) => {
          const parts = line.split("|");
          const question = parts[0];
          const correctAnswer = parts[parts.length - 1];
          const options = parts.slice(1, -1);
          return {
            question,
            options,
            correctAnswer,
          };
        });
        setQuestions(parsedQuestions);
        onQuizLoad?.(quizTitle);
      });
  }, [onQuizLoad]);

  useEffect(() => {
    if (timerRunning && timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else if (timeLeft === 0) {
      handleAnswerClick(null);
    }
  }, [timeLeft, timerRunning]);

  useEffect(() => {
    if (
      isStarted &&
      questions.length > 0 &&
      currentQuestionIndex < questions.length
    ) {
      setTimeLeft(30);
      setTimerRunning(true);
      setAnimationClass("slide-in"); // Додаємо анімацію входу
    }
  }, [currentQuestionIndex, questions, isStarted]);

  useEffect(() => {
    if (showResults && !isMuted) {
      const audio = new Audio(process.env.PUBLIC_URL + "/sounds/finish.mp3");
      audio.play().catch((e) => console.error("Could not play sound:", e));
    }
  }, [showResults, isMuted]);

  const handleAnswerClick = (selectedOption) => {
    setTimerRunning(false);
    const currentQuestion = questions[currentQuestionIndex];

    if (selectedOption === currentQuestion.correctAnswer) {
      setScore(score + 1);
      setFeedbackClass("correct");
    } else {
      setFeedbackClass("incorrect");
    }

    setAnimationClass("slide-out"); // Додаємо анімацію виходу

    setTimeout(() => {
      setFeedbackClass("");
      const nextQuestionIndex = currentQuestionIndex + 1;
      if (nextQuestionIndex < questions.length) {
        setCurrentQuestionIndex(nextQuestionIndex);
      } else {
        const endTime = new Date();
        const elapsedTimeInSeconds = Math.floor((endTime - startTime) / 1000);
        setTotalTime(elapsedTimeInSeconds);
        setShowResults(true);
        onQuizEnd();
      }
    }, 700); // Час анімації + невеликий запас
  };

  const handleNameChange = (e) => {
    let name = e.target.value;
    const ukrainianRegex = /^[А-ЯІЇЄҐа-яіїєґ\s]+$/;

    if (name.length > 0) {
      name = name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
    }

    if (name.length > 0 && !ukrainianRegex.test(name)) {
      setError("Ім'я має містити лише українські літери.");
    } else {
      setError("");
    }
    setUserName(name);
  };

  const handleStartQuiz = (e) => {
    e.preventDefault();
    const trimmedName = userName.trim();
    const words = trimmedName.split(/\s+/);

    if (trimmedName === "") {
      setError("Будь ласка, введіть своє ім'я.");
      return;
    }

    if (words.length > 2) {
      setError("Ім'я може складатися з одного або двох слів.");
      return;
    }

    if (error === "") {
      setIsStarted(true);
      setStartTime(new Date());
      onQuizStart();
    }
  };

  if (questions.length === 0) {
    return <div>Завантаження...</div>;
  }

  if (!isStarted) {
    return (
      <div className="start-container">
        <h2>{questions.length > 0 ? "Математичний квіз" : "Загальний квіз"}</h2>
        <p>Перевірте свої знання!</p>
        <form onSubmit={handleStartQuiz}>
          <input
            type="text"
            placeholder="Введіть своє ім'я"
            value={userName}
            onChange={handleNameChange}
            className={`name-input ${error ? "input-error" : ""}`}
          />
          {error && <p className="error-message">{error}</p>}
          <button type="submit" className="start-button">
            Почати Квіз
          </button>
        </form>
      </div>
    );
  }

  if (showResults) {
    return (
      <Results
        score={score}
        totalQuestions={questions.length}
        userName={userName}
        totalTime={totalTime}
      />
    );
  }

  const currentQuestion = questions[currentQuestionIndex];
  return (
    <div className={`quiz-container ${feedbackClass} ${animationClass}`}>
      <div className="timer">
        Час: <span className={timeLeft <= 5 ? "red-text" : ""}>{timeLeft}</span>
      </div>
      <Question
        question={currentQuestion.question}
        options={currentQuestion.options}
        onAnswerClick={handleAnswerClick}
        isMuted={isMuted}
      />
      <div className="question-counter">
        Питання {currentQuestionIndex + 1} з {questions.length}
      </div>
    </div>
  );
};

export default Quiz;
