import React, { useState, useEffect } from "react";
import "./App.css";
import Quiz from "./components/Quiz";

function App() {
  const [backgroundImage, setBackgroundImage] = useState("");
  const [currentTime, setCurrentTime] = useState(new Date()); // Новий стан для дати та часу

  const backgrounds = [
    "background_1.jpg",
    "background_2.jpg",
    "background_3.jpg",
  ];

  useEffect(() => {
    const randomIndex = Math.floor(Math.random() * backgrounds.length);
    const selectedBackground = backgrounds[randomIndex];
    setBackgroundImage(selectedBackground);
  }, []);

  useEffect(() => {
    if (backgroundImage) {
      document.body.style.backgroundImage = `url(${process.env.PUBLIC_URL}/images/${backgroundImage})`;
    }
  }, [backgroundImage]);

  // Оновлюємо дату та час кожну секунду
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const formatDate = (date) => {
    const options = {
      year: "numeric",
      month: "long",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit",
    };
    return date.toLocaleDateString("uk-UA", options);
  };

  return (
    <div className="App">
      <div className="date-time-display">{formatDate(currentTime)}</div>
      <header className="App-header">
        <h1>Яскравий Квіз</h1>
      </header>
      <main>
        <Quiz />
      </main>
    </div>
  );
}

export default App;
