  const formatTime = (seconds) => {
    if (seconds === null) return "‚Äî";
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins} —Ö–≤ ${secs} —Å–µ–∫`;
  };

  const getResultFeedback = () => {
    const percentage = (score / totalQuestions) * 100;
    let emoji = "";
    let message = "";

    if (percentage >= 80) {
      emoji = "ü§©";
      message = "–ß—É–¥–æ–≤–æ";
