      emoji = "üòû";
      message = "–î—É–∂–µ –ø–æ–≥–∞–Ω–æ";
    }

    return `${emoji} ${message}`;
  };

  const getResultClass = () => {
    const percentage = (score / totalQuestions) * 100;
    if (percentage >= 80) {
      return "excellent";
    } else if (percentage >= 60) {
      return "good";
    } else if (percentage >= 30) {
      return "satisfactory";
    } else {
