  };

  return (
    <div className="results-container">
      <h2 className="welcome-message">–í—ñ—Ç–∞—î–º–æ, {userName}!</h2>

      <p className={`result-text ${getResultClass()}`}>
        –í–∞—à —Ä–µ–∑—É–ª—å—Ç–∞—Ç: {getResultFeedback()}
