import React, { useState, useEffect } from 'react';
import Question from './Question';
import Results from './Results';
import questionsData from '../questions.txt';

const Quiz = ({ onQuizStart, onQuizLoad, isMuted }) => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(1);
  const [score, setScore] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [timeLeft, setTimeLeft] = useState(30);
  const [timerRunning, setTimerRunning] = useState(false);
  const [isStarted, setIsStarted] = useState(false);
  const [userName, setUserName] = useState('');
  const [feedbackClass, setFeedbackClass] = useState('');
  const [finishTime, setFinishTime] = useState(null);

  // Завантаження питань з текстового файлу
  useEffect(() => {
    fetch(questionsData)
      .then(response => response.text())
      .then(text => {
        const lines = text.trim().split('\n');
        const parsedQuestions = lines.map(line => {
          const [question, ...rest] = line.split('|');
          const correctAnswer = rest.pop();
          const options = rest;
          return {
            question,
            options,
            correctAnswer
          };
        });
        setQuestions(parsedQuestions);
        // Перевірка, що onQuizLoad існує перед викликом
        if (onQuizLoad) {
          if (parsedQuestions.length > 0) {
            const firstQuestion = parsedQuestions[0].question.split('?')[0].trim();
            onQuizLoad(firstQuestion);
          } else {
            onQuizLoad("Загальний квіз");
          }
        }
      });
  }, [onQuizLoad]);

  // Таймер для кожного питання
  useEffect(() => {
    if (timerRunning && timeLeft > 0) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else if (timeLeft === 0) {
      handleAnswerClick(null);
    }
  }, [timeLeft, timerRunning]);

  // Запуск таймера при переході до нового питання
  useEffect(() => {
    if (isStarted && questions.length > 0 && currentQuestionIndex < questions.length) {
      setTimeLeft(30);
      setTimerRunning(true);
    }
  }, [currentQuestionIndex, questions, isStarted]);

  // Відтворення звуку завершення, коли квіз закінчився
  useEffect(() => {
    if (showResults && !isMuted) {
      const audio = new Audio(process.env.PUBLIC_URL + '/sounds/finish.mp3');
      audio.play().catch(e => console.error("Could not play sound:", e));
    }
  }, [showResults, isMuted]);

  const handleAnswerClick = (selectedOption) => {
    setTimerRunning(false);
    const currentQuestion = questions[currentQuestionIndex];

    if (selectedOption === currentQuestion.correctAnswer) {
      setScore(score + 1);
      setFeedbackClass('correct');
    } else {
      setFeedbackClass('incorrect');
    }

    setTimeout(() => {
      setFeedbackClass('');
      const nextQuestionIndex = currentQuestionIndex + 1;
      if (nextQuestionIndex < questions.length) {
        setCurrentQuestionIndex(nextQuestionIndex);
      } else {
        setFinishTime(new Date());
        setShowResults(true);
      }
    }, 1000);
  };

  const handleStartQuiz = (e) => {
    e.preventDefault();
    if (userName.trim() !== '') {
      setIsStarted(true);
      onQuizStart();
    }
  };

  if (questions.length === 0) {
    return <div>Завантаження...</div>;
  }

  if (!isStarted) {
    return (
      <div className="start-container">
        <h2>
          {
            // Перевіряємо, чи є питання, перш ніж намагатися отримати їх
            questions.length > 0
              ? questions[0].question.split('?')[0].trim()
              : 'Загальний квіз'
          }
        </h2>
        <p>Перевірте свої знання!</p>
        <form onSubmit={handleStartQuiz}>
          <input
            type="text"
            placeholder="Введіть своє ім'я"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            className="name-input"
          />
          <button type="submit" className="start-button">
            Почати Квіз
          </button>
        </form>
      </div>
    );
  }

  if (showResults) {
    return (
      <Results
        score={score}
        totalQuestions={questions.length}
        userName={userName}
        finishTime={finishTime}
      />
    );
  }

  const currentQuestion = questions[currentQuestionIndex];
  return (
    <div className={`quiz-container ${feedbackClass}`}>
      <div className="timer">
        Час: <span className={timeLeft <= 5 ? 'red-text' : ''}>{timeLeft}</span>
      </div>
      <Question
        question={currentQuestion.question}
        options={currentQuestion.options}
        onAnswerClick={handleAnswerClick}
        isMuted={isMuted}
      />
    </div>
  );
};

export default Quiz;
