import React, { useState, useEffect } from "react";
import "./App.css";
import Quiz from "./components/Quiz";

function App() {
  const [backgroundImage, setBackgroundImage] = useState("");

  // Масив з назвами файлів фонових зображень
  const backgrounds = [
    "background_1.jpg",
    "background_2.jpg",
    "background_3.jpg",
  ];

  useEffect(() => {
    // Випадково обираємо один з фонів при завантаженні сторінки
    const randomIndex = Math.floor(Math.random() * backgrounds.length);
    const selectedBackground = backgrounds[randomIndex];
    setBackgroundImage(selectedBackground);
  }, []);

  // Встановлюємо фон для тіла документа
  useEffect(() => {
    if (backgroundImage) {
      document.body.style.backgroundImage = `url(${process.env.PUBLIC_URL}/images/${backgroundImage})`;
    }
  }, [backgroundImage]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Яскравий Квіз</h1>
      </header>
      <main>
        <Quiz />
      </main>
    </div>
  );
}

export default App;
