import React from "react";

// Видаляємо finishTime і додаємо totalTime
const Results = ({ score, totalQuestions, userName, totalTime }) => {
  const formatTime = (seconds) => {
    if (seconds === null) return "—";
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins} хв ${secs} сек`;
  };

  return (
    <div className="results-container">
      <h2>Вітаємо, {userName}!</h2>
      <p>
        Ваш результат: {score} з {totalQuestions}
      </p>
      <div className="score-table">
        <h3>Таблиця результатів</h3>
        <table>
          <thead>
            <tr>
              <th>Ім'я</th>
              <th>Бали</th>
              <th>Витрачений час</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{userName}</td>
              <td>{score}</td>
              <td>{formatTime(totalTime)}</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Results;
