import React from "react";

// Видалили onQuizLoad зі списку пропсів
const Results = ({ score, totalQuestions, userName, finishTime }) => {
  const formatTime = (date) => {
    if (!date) return "—";
    const options = { hour: "2-digit", minute: "2-digit", second: "2-digit" };
    return date.toLocaleTimeString("uk-UA", options);
  };

  return (
    <div className="results-container">
      <h2>Вітаємо, {userName}! Квіз завершено!</h2>
      <p>
        Ваш результат: {score} з {totalQuestions}
      </p>
      <div className="score-table">
        <h3>Таблиця результатів</h3>
        <table>
          <thead>
            <tr>
              <th>Ім'я</th>
              <th>Бали</th>
              <th>Час завершення</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{userName}</td>
              <td>{score}</td>
              <td>{formatTime(finishTime)}</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Results;
