import React, { useState, useEffect } from 'react';
import './App.css';
import Quiz from './components/Quiz';

function App() {
  const [backgroundImage, setBackgroundImage] = useState('');
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 }); // Новий стан для координат мишки

  const backgrounds = ['background_1.jpg', 'background_2.jpg', 'background_3.jpg'];

  useEffect(() => {
    const randomIndex = Math.floor(Math.random() * backgrounds.length);
    const selectedBackground = backgrounds[randomIndex];
    setBackgroundImage(selectedBackground);
  }, []);

  // Обробник руху мишки
  const handleMouseMove = (e) => {
    setMousePosition({ x: e.clientX, y: e.clientY });
  };

  // Встановлення фону та його позиції
  useEffect(() => {
    if (backgroundImage) {
      document.body.style.backgroundImage = `url(${process.env.PUBLIC_URL}/images/${backgroundImage})`;
    }

    // Обчислюємо нову позицію фону в залежності від координат мишки
    const moveX = (mousePosition.x - window.innerWidth / 2) / 50;
    const moveY = (mousePosition.y - window.innerHeight / 2) / 50;
    document.body.style.backgroundPosition = `calc(50% + ${moveX}px) calc(50% + ${moveY}px)`;
  }, [backgroundImage, mousePosition]); // Оновлюємо ефект при зміні фону або позиції мишки

  return (
    <div className="App" onMouseMove={handleMouseMove}>
      <header className="App-header">
        <h1>Яскравий Квіз</h1>
      </header>
      <main>
        <Quiz />
      </main>
    </div>
  );
}

export default App;
